import React from 'react'

function BidDetails(props) {
  const createdAt = new Date(props.bids.created_at)
  return (
    <div>
      <p>
        {props.price}<br/>
      </p>
      <p>
      <small>On {createdAt.toString()}</small>
      </p>
      <button onClick={(event) => {props.handleDeleteBid(props.id)}}>Delete</button>
    </div>
  )
}

const styles = {
  container: {
    color: 'red'
  },
  heading: {
    color: 'blue'
  }
}
export default BidDetails
